fd.rendered(function () {
    fd.field('Date').value = new Date();
    fd.field('DateFormB').value = new Date();
    fd.field('Date1FormE').value = new Date();
    fd.field('Date2FormE').value = new Date();
    fd.field('DateFormF').value = new Date();
    console.log(fd.data());
});

fd.beforeSave(function () {
    // make sure dates are valid
    fd.field('FirstDayOnSite').$on('change', checkFirstLastDate);
    fd.field('LastDayOnSite').$on('change', checkFirstLastDate);
});

function checkFirstLastDate() {
    // prevent saving if FirstDayOnSite > LastDayOnSite
    if (fd.field('FirstDayOnSite').value > fd.field('LastDayOnSite').value) {
        throw Error('First Day on Site must be before Last Day on Site.');
    }
}

