//The following code was used in editing and debugging. However, it was determined that simplifying and condensing
//everything into the internal editor would cause less of a chance for things to break


//The following code allows javascript to be run from an external api for ease of editing
fd.beforeRender(function(vue){
    var script = document.createElement('script'); 
    script.src = '//code.jquery.com/jquery-1.11.0.min.js'; 
    document.getElementsByTagName('head')[0].appendChild(script);
    
    jQuery(document).ready(function($){
        var imported = document.createElement('script');
        imported.src = 'https://tce-innovation.github.io/Subcontractor-Automation/src/initialization.js';
        document.head.appendChild(imported);
    });
});

